import { useEffect, useState } from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableContainer from '@mui/material/TableContainer';
import Paper from '@mui/material/Paper';
import SuccessRow from '../components/SuccessRow';
import ErrorRow from '../components/ErrorRow';

const PageTwo = ({ data = [] }) => {
  const [rows, setRows] = useState();

  //   const processUrl = async (src) => {
  //     const request = new XMLHttpRequest();
  //     request.open('GET', src, true);
  //     request.responseType = 'blob';
  //     request.send();
  //     request.onload = function () {
  //       if (request.status === 200) {
  //         console.log('success');
  //         return true;
  //       } else {
  //         console.log('fail');
  //         return false;
  //       }
  //     };
  //   };

  async function processUrl(src) {
    var result = await fetch(src);
    if (result.ok) {
      await result.text();
      return true;
    }
    return false;
  }

  const processImage = async (src) => {
    let img;
    const imageLoadPromise = new Promise((resolve) => {
      img = new Image();
      img.onload = resolve;
      img.src = src;
    });

    await imageLoadPromise;
    console.log('onload', img.width);
    return {
      width: img.width,
      height: img.height,
    };
  };

  const createRow = (row) => {
    console.log('row', row);
    if (row.width && row.width !== 0) {
      return <SuccessRow row={row} />;
    }
    return <ErrorRow row={row} />;
  };

  const aggregateData = () => {
    const mergedData = data.map(async (row) => {
      const isValidUrl = processUrl(row.url);
      console.log('isValidUrl', isValidUrl);
      if (isValidUrl) {
        const dimensions = await processImage(row.url);
        console.log('dimensions', dimensions);
        return { ...row, width: dimensions.width, height: dimensions.height };
      } else {
        return { ...row, width: 0, height: 0 };
      }
    });
    console.log('mergedData', mergedData);
    setRows(mergedData);
  };

  useEffect(() => {
    aggregateData();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <TableContainer component={Paper}>
      <Table
        sx={{ minWidth: 650, maxWidth: 800, margin: '0 auto' }}
        aria-label="simple table"
      >
        <TableBody>{rows && rows.map((row) => createRow(row))}</TableBody>
      </Table>
    </TableContainer>
  );
};

export default PageTwo;
